class EncounterMember{
    constructor(memberIndex, name, initiative, statusesStart, statusesEnd, statusesGeneral){
        this.index = memberIndex;
        this.name = name || "";
        this.initiative = initiative || null;
        this.statuses = [(statusesStart || []), (statusesGeneral || []), (StatusesEnd || [])];
    };

    addStatus = (status) => {
        this.statusesStart.push(status);
    }

    removeStatus = (statusId) => {
        let removeIndex = -1;
        for (let i = 0; i < this.statusesStart.length; i++){
            if(this.statusesStart[i].id == statusId){removeIndex=i; break;}
        }
        this.statusesStart.splice(removeIndex, 1);
    }
}


export default EncounterMember;
