import React from "react"
import TimelineCard from "./TimelineCard"
import styles from "./CSS/TimelineView.module.css"

class TimelineView extends React.Component {
    constructor(props){
        super(props)
        this.state = {iterations: 2};
    }

    render(){
        return(
            <div className={styles.TimelineViewContainer}>
                <div className={styles.TimelineToolbar}></div>
                {this.renderListItems(this.props.memberList)}
            </div>

        )
    }

    renderListItems = list => {
        // Shift member list into initiative list, discarding anyone without an initiative
        let initiativeOrder = [];
        list.forEach(i => {
            if (i.initiative != null) {initiativeOrder.push(i);}
        })

        // Sort initiative order
        // Note: multiply initiative by 1 to force JS to convert them to numbers during the comparison
        initiativeOrder.sort((a, b) => (a.initiative*1 < b.initiative*1) ? 1 : -1); 

        // Render list into TimelineCard React Components
        let toRender = []

        for (let i = 0; i < this.state.iterations; i++) {
            toRender.push(initiativeOrder.map(i => {
                return <TimelineCard member={i}/>
        }))}

        return toRender;
    }
}

export default TimelineView;
