class EncounterMember{
    constructor(memberIndex, name, initiative){
        this.index = memberIndex;
        this.name = name || "";
        this.initiative = initiative || null;
        this.statuses = {start: [], general: [], end: []};
    };

    addStatus = (status) => {
        this.statuses.start.push(status);
    }

    removeStatus = (statusId) => {
        let removeIndex = -1;
        for (let i = 0; i < this.statuses[0].length; i++){
            if(this.statuses.start[i].id === statusId){removeIndex=i; break;}
        }
        this.statuses[0].splice(removeIndex, 1);
    }
}


export default EncounterMember;
