import './CSS/App.css';
import EncounterMember from './EnounterMember.js';
import TimelineView from "./TimelineView.js";
import MemberListView from "./MemberListView.js";


function App() {

  let memberList = [];
  let initiativeOrder = []; 

  // manually fill the member list for testing
  memberList.push(new EncounterMember("test0", 12));
  memberList.push(new EncounterMember("test1", 3));
  memberList.push(new EncounterMember("test2"));
  memberList.push(new EncounterMember("test3", 3));
  memberList.push(new EncounterMember("test4", 8));

  // Sort and fill initiative order
  initiativeOrder = [];
  let tempList = [];
  memberList.forEach(e => {
    if (e.initiative != null) tempList.push(e);
  })

  while (tempList.length > 0){
    let highest = -99;
    let highestIndex = -1;
    for (let i = 0; i < tempList.length; i++){
      if (tempList[i].initiative > highest){
        highest = tempList[i].initiative;
        highestIndex = i;
      }
    }
    initiativeOrder.push(tempList[highestIndex]);
    tempList.splice(highestIndex, 1);
  }


  return (
    <div className="App">

      <div>
        <TimelineView initiativeOrder={initiativeOrder} iterations={4}/>
      </div>

      <div>
        <MemberListView memberList={memberList}/>
      </div>

    </div>
  );
}

export default App;
