import React from "react"
import styles from "./CSS/TimelineCard.module.css"

class TimelineCard extends React.Component{
    constructor(props){
        super(props)
        this.state = {initiative: this.props.member.initiative}
        this.handleChange = this.handleChange.bind(this);
        this.submitChange = this.submitChange.bind(this);
    }

    handleChange(event) {
        this.props.onInitiativeChange(event.target.value, this.props.member.index)
    }

    submitChange(event) {
        if (event.key === "Enter"){
            this.setState({initiative: this.props.member.initiative})
        }
    }

    render(){
        return(
            <div className={styles.TimelineCardContainer}>
                <div className={styles.TimelineTitle}>
                    <p className={styles.Name}>{this.props.member.name}</p>
                    <input className={styles.InitiativeBox} type="number" value={this.props.member.initiative} onChange={this.handleChange} onKeyDown={this.submitChange}/>
                </div>
            </div>
        );
    }
}

export default TimelineCard;
