import React from "react"
import styles from "./CSS/TimelineCard.module.css"

class TimelineCard extends React.Component{
    constructor(props){
        super(props)
        this.state = {initiative}
        this.handleChange = this.handleChange.bind(this);
        this.submitChange = this.submitChange.bind(this);
    }

    handleChange(event) {
        this.setState({initiative: event.target.value});
    }

    render(){
        return(
            <div className={styles.TimelineCardContainer}>
                <div className={styles.TimelineTitle}>
                    <p className={styles.Name}>{this.props.member.name}</p>
                    <input className={styles.InitiativeBox} type="number" value={this.state.initiative} onChange={this.handleChange} onKeyDown={this.submitChange}/>
                </div>
            </div>
        );
    }
}

export default TimelineCard;
