import React from "react"
import TimelineCard from "./TimelineCard"

class TimelineView extends React.Component {
    render(){
        return(
            this.renderListItems(this.props.initiativeOrder)
        )
    }

    renderListItems = list => {
        let toRender = []
        for (let i = 0; i < this.props.iterations; i++) {
            toRender.push(list.map(i => {
            let name = i.name;
            let initiative = i.initiative;
            let statusesStart = i.statusesStart;
            let statusesEnd = i.statusesEnd;
            let statusesGeneral = i.statusesGeneral;
            return <TimelineCard name={name} initiative={initiative} statusesStart={statusesStart} statusesEnd={statusesEnd} statusesGeneral={statusesGeneral}/>
        }))}

        return toRender;
    }
}

export default TimelineView;
