import React from "react"
import TimelineCard from "./TimelineCard"
import styles from "./CSS/TimelineView.module.css"

class TimelineView extends React.Component {
    constructor(props){
        super(props)
        this.state = {iterations: 2};
        this.handleChange = this.handleChange.bind(this);
    }

    render(){
        return(
            <div className={styles.TimelineViewContainer}>
                <div className={styles.TimelineToolbar}>

                </div>

                <div className={styles.SmallBar}> 
                    <div className={styles.TimelineSummary}>

                    </div>
                    <div className={styles.IterationsField}>
                        <input type="number" value={this.state.iterations} onChange={this.handleChange}/>
                    </div>
                </div>

                <div className={styles.TimelineMain}>
                    {this.renderListItems(this.props.memberList)}
                </div>

                <div className={styles.TimelineCurrent}>

                </div>

            </div>

        )
    }

    handleChange(event){
        this.setState({initiative: event.target.value});
    }

    handleInitiativeChange = (newInitiative, memberIndex) => {
        this.props.onInitiativeChange(newInitiative, memberIndex);
    }

    renderListItems = list => {
        // Shift member list into initiative list, discarding anyone without an initiative
        let initiativeOrder = [];
        list.forEach(i => {
            if (i.initiative != null) {initiativeOrder.push(i);}
        })

        // Sort initiative order
        // Note: multiply initiative by 1 to force JS to convert them to numbers during the comparison
        initiativeOrder.sort((a, b) => (a.initiative*1 < b.initiative*1) ? 1 : -1); 

        // Render list into TimelineCard React Components
        let toRender = []

        for (let i = 0; i < this.state.iterations; i++) {
            toRender.push(initiativeOrder.map(i => {
                return <TimelineCard member={i} onInitiativeChange={this.handleInitiativeChange}/>
            }))
            toRender.push(<div className={styles.Separator}></div>)
        }

        return toRender;
    }
}

export default TimelineView;
