import React from "react";
import styles from "./CSS/StatusInventory.module.css";
import { Droppable } from "react-beautiful-dnd";
import StatusIcon from "./StatusIcon";
import Status from "./Status";

class StatusInventory extends React.Component {
    constructor(props){
        super(props)
        this.state={
            statuses: []
        }

        this.state.statuses.push(new Status());
    }

    renderStatusIcons(){
        let toRender = [];

        toRender.push(this.state.statuses.map(({id}, index) => {
            return <StatusIcon key={id} draggableId={id} index={index}/>    
        }));
    }

    render(){
        return(
            <Droppable droppableId="statusInventory">
                {(provided) => (
                    <ul className={styles.StatusInventory} {...provided.droppableProps} ref={provided.innerRef}>
                        {this.renderStatusIcons}
                        {provided.placeholder}
                    </ul>
                )
                }
            </Droppable>
        );
    }
}

export default StatusInventory;
