import './CSS/App.css';
import EncounterMember from './EnounterMember.js';
import TimelineView from "./TimelineView.js";
import MemberListView from "./MemberListView.js";
import React from 'react';


class App extends React.Component {

  constructor (props){
    super(props);
    this.memberList = [];
    this.initiativeOrder = []; 

    // manually fill the member list for testing
    this.memberList.push(new EncounterMember("test0", 12));
    this.memberList.push(new EncounterMember("test1"));
    this.memberList.push(new EncounterMember("test2"));
    this.memberList.push(new EncounterMember("test3", 3));
    this.memberList.push(new EncounterMember("test4"));

    // Sort and fill initiative order
    this.initiativeOrder = [];
    let tempList = [];
    this.memberList.forEach(e => {
      if (e.initiative != null) tempList.push(e);
    })

    while (tempList.length > 0){
      let highest = -99;
      let highestIndex = -1;
      for (let i = 0; i < tempList.length; i++){
        if (tempList[i].initiative > highest){
          highest = tempList[i].initiative;
          highestIndex = i;
        }
      }
      this.initiativeOrder.push(tempList[highestIndex]);
      tempList.splice(highestIndex, 1);
    }
  }

  handleInitiativeChange = (newInitiative, memberIndex) => {
    this.memberList[memberIndex].initiative = newInitiative;
  }

  render(){
    return (
      <div className="App">

        <div className = "TopBar">

        </div>

        <div className = "MainView">
          <div className="TimelineContainer">
            <TimelineView initiativeOrder={this.initiativeOrder} onInitiativeChange={this.onInitiativeChange} iterations={2}/>
          </div>

          <div className="MemberListContainer">
            <MemberListView memberList={this.memberList} onInitiativeChange={this.onInitiativeChange}/>
          </div>
        </div>

        <div className = "BottomBar">

        </div>


      </div>
    );
  }
}

export default App;
