import React from "react";
import styles from "./CSS/StatusEditWindow.module.css";
import Status from "./Status";

class StatusEditWindow extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            statusData: this.props.statusData
        }
    }

    render(){
        let showWindow = false;

        /*
        let displayStatus = {};
        let title = "";
        let desc = "";
        let icon = "";
        /*if (this.props.targetStatus != null && this.props.targetStatus.length == 2){
            displayStatus = this.props.getStatusData(this.props.targetStatus[0], this.props.targetStatus[1]);
            if (displayStatus && Object.hasOwn(displayStatus, 'status')){

                title = displayStatus.status.name;
                desc = displayStatus.status.desc;
                icon = displayStatus.status.icon;
                showWindow = true;
            }
        }*/
        if (this.props.statusData != null && Object.hasOwn(this.props.statusData, 'status')){
            /*title = this.props.statusData.status.name;
            desc = this.props.statusData.status.desc;
            icon = this.props.statusData.status.icon;
            showWindow = true;*/
            /*this.setState({
                showWindow: true,
                title: this.props.statusData.status.name,
                desc: this.props.statusData.status.desc,
                icon: this.props.statusData.status.icon
            });*/
            showWindow = true;
        }

        if (showWindow) {
            return(
                <div className={styles.WindowContainer}>
                    <div className={styles.TitleBar}>
                        <img src={this.state.statusData.status.icon} className={styles.Icon}/>
                        <div className={styles.TitleWrapper}>
                            <input type="text" className={styles.Title} value={this.state.statusData.status.name}/>
                            <div className={styles.TimerWrapper}>
                                <label htmlFor="timerField" className={styles.TimerLabel}>Timer: </label>
                                <input id="timerField" type="number" className={styles.TimerField}></input>
                            </div>
                        </div>
                        <div className={styles.CloseButtonWrapper}>
                            <button onClick={this.props.clearTargetStatus} className={styles.CloseButton}>X</button>
                        </div>   
                    </div>
                    <div className={styles.ContentWrapper}>
                        <textarea className={styles.Description} value={this.state.statusData.status.desc}/>
                    </div>
                </div>
            );
        } else {
            return;
        }

    }
}

export default StatusEditWindow;
