import React from "react"
import MemberCard from './MemberCard.js';

class MemberListView extends React.Component {
    render(){
        return(
            this.renderMemberList(this.props.memberList)
        );
    }

    renderMemberList = list => {
        let toRender = [];

        for (let i = 0; i < this.props.iterations; i++){
            toRender.push(list.map(i => {
                let name = i.name;
                let initiative = i.initiative;
                let statusesStart = i.statusesStart;
                let statusesEnd = i.statusesEnd;
                let statusesGeneral = i.statusesGeneral;
                return <MemberCard name={name} initiative={initiative} statusesStart={statusesStart} statusesEnd={statusesEnd} statusesGeneral={statusesGeneral}/>
            }))
        }

        return toRender;
    }

}

export default MemberListView;
