import React from "react"
import TimelineCard from "./TimelineCard"

class TimelineView extends React.Component {
    render(){
        return(
            this.renderListItems(this.props.memberList)
        )
    }

    renderListItems = list => {
        // Shift member list into temp list, discarding anyone without an initiative
        let initiativeOrder = [];
        list.forEach(i => {
            if (i.initiative != null) initiativeOrder.push(i);
        })

        // Sort temp list into an initiative order


        // Render list into TimelineCard React Components
        let toRender = []

        for (let i = 0; i < this.props.iterations; i++) {
            toRender.push(initiativeOrder.map(i => {
                return <TimelineCard member={i}/>
        }))}

        return toRender;
    }
}

export default TimelineView;
