import React from "react"
import styles from "./CSS/TimelineCard.module.css"

class TimelineCard extends React.Component{
    constructor(props){
        super(props)
        let i = this.props.member.initiative;
        this.state = {initiative: i};

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({initiative: event.target.value});
        this.props.onInitiativeChange(event.target.value, this.props.member.index)
    }

    render(){
        return(
            <div>
                <p>{this.props.member.name}</p>
                <input type="number" value={this.state.initiative} onChange={this.handleChange}/>
            </div>
        );
    }
}

export default TimelineCard;
