import React from "react"
import styles from "./CSS/MemberCard.module.css"

class MemberCard extends React.Component {
    constructor(props){
        super(props);
        //let i = this.props.member.initiative;
        //this.state = {initiative: i}
        this.state = {initiative: this.props.member.initiative};

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({initiative: event.target.value});
        this.props.onInitiativeChange(event.target.value, this.props.member.index)
    }

    render(){
        return(
            <div className={styles.MemberCardContainer}>
                <div className={styles.Title}>
                    <p className={styles.Name}>{this.props.member.name}</p>
                    <input className={styles.InitiativeField} type="number" value={this.props.initiative} onChange={this.handleChange}/>
                </div>
            </div>
        );
    }
}

export default MemberCard;
